create schema labs;use labs;create table Student (  sID INT NULL,  sName VARCHAR(45) NULL,  score INT NULL,  SSSize INT NULL,  primary key (sID));create table University (  uName VARCHAR(45) NULL,  uCity VARCHAR(45),  enrollment INT NULL,  primary key (uName));create table Apply (  sID INT NULL,  uName VARCHAR(45) NULL,  degree VARCHAR(45) NULL,  decision CHAR NULL,  primary key (sID,uName,degree),  foreign key (sID) references Student(sID),  foreign key (uName) references University(uName));
